def _start_voice_server() -> bool:
    """Start the voice server on-demand and wait for health check."""
    import subprocess
    start_script = Path.home() / ".claude" / "VoiceServer" / "start.sh"
    if not start_script.exists():
        log("Voice server start.sh not found")
        return False
    try:
        result = subprocess.run(
            ["bash", str(start_script)],
            capture_output=True, text=True, timeout=20,
        )
        return result.returncode == 0
    except Exception as e:
        log(f"Voice server start failed: {e}")
        return False


def _stop_voice_server() -> None:
    """Stop the voice server after use."""
    import subprocess
    stop_script = Path.home() / ".claude" / "VoiceServer" / "stop.sh"
    if not stop_script.exists():
        return
    try:
        subprocess.run(
            ["bash", str(stop_script)],
            capture_output=True, text=True, timeout=10,
        )
    except Exception:
        pass


def notify(message: str) -> bool:
    """Send a voice notification via the PAI voice server (on-demand start/stop)."""
    if not message:
        return False

    # Start voice server on-demand
    if not _start_voice_server():
        log("Could not start voice server, skipping voice notification")
        return False

    try:
        resp = httpx.post(
            VOICE_SERVER_URL,
            json={
                "message": message,
                "voice_id": VOICE_ID,
                "voice_settings": {
                    "stability": 0.5,
                    "similarity_boost": 0.75,
                    "style": 0.2,
                    "speed": 1.1,
                },
            },
            timeout=30.0,
        )
        return resp.status_code == 200
    except Exception as e:
        log(f"Voice notification failed: {e}")
        return False
    finally:
        # Always stop voice server after speaking
        _stop_voice_server()
